// Helpers
// ---------------
var _ = require('lodash');

module.exports = {

  // returns a query
  query: function(obj) {
    var query = {};
    if(obj.id) {
      query[obj.idAttribute] = obj.id;
    } else {
      // query becomes the objects attributes if id is not defined
      query = _.clone(obj.attributes);
    }
    return query;
  }

};